UPDATE npcloot SET [definition]=@definitionID, [lootdefinition]=@lootdefinitionID, [quantity]=1, [probability]=0.125, [repackaged]=1, [dontdamage]=1, [minquantity]=1 WHERE [id]=@npclootID;

