UPDATE npcloot SET [definition]=@definitionID, [lootdefinition]=@lootdefinitionID, [quantity]=1, [probability]=0.015, [repackaged]=0, [dontdamage]=0, [minquantity]=1 WHERE [id]=@npclootID;

